INSERT INTO `frontend_settings` (`id`, `key`, `value`) VALUES ('1', 'banner_title', 'Unlock Your Potential in the World of  Sales_and_Marketting'), ('2', 'banner_sub_title', 'Study any topic, related sales &amp; marketting and explore hundreds of free courses.'), ('4', 'about_us', '<p></p><h2><span xss=\"removed\">About us</span></h2>'), ('10', 'terms_and_condition', '<h2>Terms and Condition</h2>'), ('11', 'privacy_policy', '<p></p><p></p><h2><span xss=\"removed\">Privacy Policy</span></h2>'), ('13', 'theme', 'default-new'), ('14', 'cookie_note', 'This website uses cookies to personalize content and analyse traffic in order to offer you a better experience.'), ('15', 'cookie_status', 'active'), ('16', 'cookie_policy', '<h1>Cookie policy</h1><ol><li>Cookies are small text files that can be used by websites to make a user\'s experience more efficient.</li><li>The law states that we can store cookies on your device if they are strictly necessary for the operation of this site. For all other types of cookies we need your permission.</li><li>This site uses different types of cookies. Some cookies are placed by third party services that appear on our pages.</li></ol>'), ('17', 'banner_image', '{\"home_1\":\"164f98ba2892701d67ee2aa3ace7a1f6.png\"}'), ('18', 'light_logo', 'logo-light.png'), ('19', 'dark_logo', '3ea7c7dc755760b1847a7e90b6a764a9.png'), ('20', 'small_logo', 'logo-light-sm.png'), ('21', 'favicon', 'favicon.png'), ('22', 'recaptcha_status', '1'), ('23', 'recaptcha_secretkey', '6Lc6e7QoAAAAAK0Uzg2BeNR4bXVos66eFuvqqm2s'), ('24', 'recaptcha_sitekey', '6Lc6e7QoAAAAACHjpxsVgFTiL7NaTos2rqqKH0Hd'), ('25', 'refund_policy', '<h2><span xss=\"removed\">Refund Policy</span></h2>'), ('26', 'facebook', 'https://www.facebook.com/Y4DTeam'), ('27', 'twitter', 'http://twitter.com/intent/follow?source=followbutton&variant=1.0&screen_name=y4dteam'), ('28', 'linkedin', 'https://www.linkedin.com/company/y4dteam'), ('31', 'blog_page_title', 'Where possibilities begin'), ('32', 'blog_page_subtitle', 'We’re a leading marketplace platform for learning and teaching online. Explore some of our most popular content and learn something new.'), ('33', 'blog_page_banner', 'blog-page.png'), ('34', 'instructors_blog_permission', '0'), ('35', 'blog_visibility_on_the_home_page', '1'), ('38', 'motivational_speech', '[{\"title\":\"Y4D Team\",\"description\":\" Young team of Y4D is doing a wonderful job. Now the tagline \\u201cab baari hai humari\\u201d is not only the tagline of  Y4D foundation, Should be India\'s tagline too.                       \",\"image\":\"4R2fCSAqJox753gmILBZ.png\"}]'), ('39', 'home_page', 'home_1'), ('40', 'contact_info', '{\"email\":\"lmslearning@y4d.ngo\",\"phone\":\"082828 28811\",\"address\":\"402, The Onyx, near Euro School, Wakad, Pune, Maharashtra 411057\",\"office_hours\":\"\"}'), ('41', 'website_faqs', '[{\"question\":\"What Is LMS?\",\"answer\":\"A learning management system &#40;LMS&#41; is a software application for the administration, documentation, tracking, reporting, automation, and delivery of educational courses, training programs, materials or learning and development programs. The learning management system concept emerged directly from e-Learning\"},{\"question\":\"what is women empowerment?\",\"answer\":\"What is women\'s empowerment? Women\'s empowerment can be defined to promoting women\'s sense of self-worth, their ability to determine their own choices, and their right to influence social change for themselves and others.\"},{\"question\":\"What is the role of digital education in transforming education?\",\"answer\":\"Digital transformation in the education industry helps provide flexibility to continue teaching and learning from a remote location. Also, it helps to offer a flexible course structure and allows students to learn anytime and anywhere. Moreover, it enhances the overall teacher and student learning experience.\"},{\"question\":\"what is social reform?\",\"answer\":\"The social reform, a movement was started by Raja Ram Mohan Roy in East India to eradicate child marriage, sati pratha, purdah system, polytheism, and polygamy from the Indian society. The social reform movement in 1820 introduced legislation guidelines for the development of a society free from casting inequality.\"},{\"question\":\"how can we empower women?\",\"answer\":\"Women are known to contribute significantly to economics in the form of business, entrepreneurial work, or unpaid labor (sadly!). While women living in some parts of the developed countries have the role of decision maker and influencers, gender discrimination remains a debilitating social issue in many parts of the world, and those subaltern women are often alarmingly affected by poverty, discrimination, and other forms of vulnerable exploitations.   \\r\\n\\r\\nAs any developing nation would agree, sustainable economic growth is unthinkable without women empowerment, and measures for gender inclusion is the driving factor of social progress and economic growth. Working women have an enormous contribution to education, health, and wellness and therefore achieving gender equality is indispensable to holistic developments.\"}]');
